#!/bin/bash
sudo apt update && sudo apt upgrade -y && sudo sed -i 's/PermitRootLogin prohibit-password/PermitRootLogin yes/' /etc/ssh/sshd_config && sudo systemctl restart sshd && sudo fallocate -l 1G /swapfile && sudo chmod 600 /swapfile && sudo mkswap /swapfile && sudo swapon /swapfile && echo '/swapfile none swap sw 0 0' | sudo tee -a /etc/fstab && echo 'vm.swappiness=100' | sudo tee -a /etc/sysctl.conf && echo 'vm.vfs_cache_pressure=100' | sudo tee -a /etc/sysctl.conf && sudo sysctl -p && sudo apt autoremove --purge snapd -y && sudo apt-mark hold snapd && echo 'cloud-init cloud-init/datasources string None' | sudo debconf-set-selections && sudo dpkg-reconfigure -f noninteractive cloud-init && sudo apt autoremove --purge cloud-init -y && sudo rm -rf /etc/cloud/ && sudo rm -rf /var/lib/cloud/ && echo "Done"